package fr.hokanosekai;import fr.hokanosekai.commands.SetCooldownTimer;import fr.hokanosekai.listeners.PlayerListener;import fr.hokanosekai.managers.CooldownManager;import fr.hokanosekai.utils.FileUtils;import org.bukkit.Bukkit;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.plugin.java.JavaPlugin;public class Main extends JavaPlugin {    public static int timer;    private static Main INSTANCE;    private CooldownManager cooldownManager;    String fileName = "EnderPearlCoolDown";    FileUtils mainFile = new FileUtils(this);    FileConfiguration fConfig = mainFile.getConfig(fileName);    @Override    public void onEnable() {        INSTANCE = this;        mainFile.createFile(fileName);        timer = fConfig.getInt("timer");        cooldownManager = new CooldownManager();        Bukkit.getPluginManager().registerEvents(new PlayerListener(), this);        this.getCommand("SetCooldownTimer").setExecutor(new SetCooldownTimer());    }    public static Main getInstance() {        return INSTANCE;    }    public CooldownManager getCooldownManager() {        return cooldownManager;    }    public String getFileName() {        return fileName;    }    public FileUtils getMainFile() {        return mainFile;    }}