package fr.hokanosekai.commands;import fr.hokanosekai.Main;import fr.hokanosekai.utils.FileUtils;import org.bukkit.Bukkit;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.entity.Player;public class SetCooldownTimer implements CommandExecutor {    Main plugin = Main.getInstance();    FileUtils mainFile = plugin.getMainFile();    FileConfiguration fConfig = mainFile.getConfig(plugin.getFileName());    @Override    public boolean onCommand(CommandSender s, Command cmd, String label, String[] args) {        if (!(s instanceof Player)){            return true;        }        Player p = ((Player) s).getPlayer();        if (!p.isOp()){            p.sendMessage("§cVous devez être op");            return true;        }        if (args.length != 1){            p.sendMessage("§cUsage /setcooldowntimer <integer of time in second>");            return true;        }        fConfig.set("timer", Integer.parseInt(args[0]));        mainFile.save(mainFile.getFile(plugin.getFileName()), fConfig);        Main.timer = fConfig.getInt("timer");        p.sendMessage("§aTimer changed succesfully to §e"+args[0]+"s");        return true;    }}