package fr.hokanosekai.listeners;import fr.hokanosekai.Main;import org.bukkit.Material;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.block.Action;import org.bukkit.event.player.PlayerInteractEvent;public class PlayerListener implements Listener {    Main plugin = Main.getInstance();    @EventHandler    public void onUseEnderPearl(PlayerInteractEvent event){        Player player = event.getPlayer();        Material material = event.getMaterial();        Action action = event.getAction();        if (action == Action.RIGHT_CLICK_AIR || action == Action.RIGHT_CLICK_BLOCK) {            if (material.equals(Material.ENDER_PEARL)){                if (plugin.getCooldownManager().isPlayerInCooldownMap(player)) {                    player.sendMessage("§cYou can reuse this item in §e" + plugin.getCooldownManager().getPlayerCooldownMap(player) + "s");                    event.setCancelled(true);                } else {                    plugin.getCooldownManager().addPlayerToCooldownMap(player, Main.timer);                }            }        }    }}