package fr.hokanosekai.managers;import fr.hokanosekai.Main;import fr.hokanosekai.utils.FileUtils;import org.bukkit.Bukkit;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.entity.Player;import org.bukkit.scheduler.BukkitRunnable;import java.util.HashMap;import java.util.Map;import java.util.UUID;public class CooldownManager {    Main plugin = Main.getInstance();    private final Map<UUID, Integer> playerCooldownMap = new HashMap<>();    FileUtils mainFile = plugin.getMainFile();    FileConfiguration fConfig = mainFile.getConfig(plugin.getFileName());    public CooldownManager(){        new BukkitRunnable(){            @Override            public void run() {                if (!playerCooldownMap.isEmpty()) {                    for (UUID uuid : playerCooldownMap.keySet()) {                        if (playerCooldownMap.get(uuid) == 1) {                            playerCooldownMap.remove(uuid);                            Bukkit.getPlayer(uuid).sendMessage("Â§aYou can reuse ender pearl");                            continue;                        }                        playerCooldownMap.put(uuid, playerCooldownMap.get(uuid) - 1);                    }                }            }        }.runTaskTimer(plugin, 0, 20);    }    public void addPlayerToCooldownMap(Player player,int timer){        playerCooldownMap.put(player.getUniqueId(), timer);    }    public boolean isPlayerInCooldownMap(Player player){        return playerCooldownMap.containsKey(player.getUniqueId());    }    public Integer getPlayerCooldownMap(Player player) {        return playerCooldownMap.get(player.getUniqueId());    }}