package fr.hokanosekai.utils;import fr.hokanosekai.Main;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.configuration.file.YamlConfiguration;import java.io.File;import java.io.IOException;public class FileUtils {    Main plugin;    public FileUtils(Main plugin) { this.plugin = plugin; }    public void createFile(String fileName){        if (!plugin.getDataFolder().exists()){            plugin.getDataFolder().mkdir();        }        File file = new File(plugin.getDataFolder(), fileName + ".yml");        if (!file.exists()){            try {                file.createNewFile();                FileConfiguration fConfig = getConfig(fileName);                fConfig.set("timer", 5);                Main.timer = 5;                save(getFile(fileName), fConfig);            }catch (IOException exception){                exception.printStackTrace();            }        }    }    public File getFile(String fileName){        return new File(plugin.getDataFolder(), fileName + ".yml");    }    public FileConfiguration getConfig(String fileName){        return YamlConfiguration.loadConfiguration(getFile(fileName));    }    public void save(File file, FileConfiguration config){        try {            config.save(file);        } catch (IOException e){            e.printStackTrace();        }    }}